import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import User from "./User";

const Food = () => {
    const params = useParams();
    const navigate = useNavigate();
    const [food, setFood] = useState([
        {
            name: "Cake",
            price: "500",
            qty: 1,
            dis: "Words that best describe Pizza: Tasty: Satisfying: Versatile: Delicious: Crispy: Cheesy: Crave-worthy: Aromatic: Mouth-watering: Tempting: Comforting: Quick: ...",
            image: "https://images.pexels.com/photos/291528/pexels-photo-291528.jpeg?auto=compress&cs=tinysrgb&w=600"
        },
        {
            name: "Milk",
            price: "60",
            qty: 1,
            dis: "Words that best describe Pizza: Tasty: Satisfying: Versatile: Delicious: Crispy: Cheesy: Crave-worthy: Aromatic: Mouth-watering: Tempting: Comforting: Quick: ...",
            image: "https://images.pexels.com/photos/5840409/pexels-photo-5840409.jpeg?auto=compress&cs=tinysrgb&w=600"
        },
        {
            name: "Pizza",
            price: "200",
            qty: 1,
            dis: "Words that best describe Pizza: Tasty: Satisfying: Versatile: Delicious: Crispy: Cheesy: Crave-worthy: Aromatic: Mouth-watering: Tempting: Comforting: Quick: ...",
            image: "https://images.pexels.com/photos/905847/pexels-photo-905847.jpeg"
        },
        {
            name: "Bread",
            price: "130",
            qty: 1,
            dis: "Words that best describe Pizza: Tasty: Satisfying: Versatile: Delicious: Crispy: Cheesy: Crave-worthy: Aromatic: Mouth-watering: Tempting: Comforting: Quick: ...",
            image: "https://images.pexels.com/photos/1586947/pexels-photo-1586947.jpeg?auto=compress&cs=tinysrgb&w=800"
        },
        {
            name: "Biscuit",
            price: "30",
            qty: 1,
            dis: "Words that best describe Pizza: Tasty: Satisfying: Versatile: Delicious: Crispy: Cheesy: Crave-worthy: Aromatic: Mouth-watering: Tempting: Comforting: Quick: ...",
            image: "https://images.pexels.com/photos/28895988/pexels-photo-28895988/free-photo-of-delicious-cheesecake-with-lotus-biscuit-topping.jpeg?auto=compress&cs=tinysrgb&w=600"
        }
    ])

    useEffect( () => {
       const auth = localStorage.getItem('auth');
       if(!(auth || visit)) {
          navigate('/user');
       }
    },[])

    return (
        <div className="mx-2 mx-md-5 mb-3" style={{ marginTop: '100px' }}>

            <h3 className="text-center">FOOD LIST</h3>

            {
                food.map((data, index) => (
                    <>
                        <div className="card mb-3">
                            <img src={`${data.image}`} className="card-img-top " alt="..." style={{ maxHeight: '400px' }} />
                            <div className="card-body">
                                <h5 className="card-title d-inline-block">Name :</h5>
                                <p className="card-text d-inline-block">
                                    {data.name}       </p>
                                <h6></h6>
                                <h5 className="card-title d-inline-block">Price : </h5>
                                <p className="card-text d-inline-block">
                                    {data.price}       </p>
                                    <h5></h5>
                                    <h5 className="card-title d-inline-block">Qty : </h5>
                                <p className="card-text d-inline-block">
                                    {data.qty}       </p>
                                <h5 className="card-title">Discription: </h5>
                                <p className="card-text">
                                    {data.dis}        </p>
                                <p className="card-text">
                                    <small className="text-muted">Last updated 3 mins ago</small>
                                </p>
                            </div>
                        </div>

                    </>
                ))
            }
        </div>

    )
}
export default Food;
