import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Offcanvas from 'react-bootstrap/Offcanvas';
import { useLocation } from "react-router-dom";
import Logo from "./Logo";

const Header = () => {
    const [search, setSearch] = useState();
    const location = useLocation();

    const show = () => {
        <Logo/>
    }
    return (
        <>
            {['sm'].map((expand) => (
                <Navbar bg="primary" data-bs-theme="dark" key={expand} expand={expand} className="bg-body-tertiary mb-3 position-fixed top-0 w-100"
                    style={{ zIndex: 1 }}>
                    <Container fluid>
                    <img src="./metro-logo.jpg" className="mx-auto d-block me-2" alt="..." style={{
                        maxHeight:'70px',maxWidth:'70px'
                    }} onClick={show}/>

                        <Navbar.Brand href="#" className="fw-bold fs-4"
                            style={{ letterSpacing: '1px', fontFamily: 'serif', color: 'silver' }}>METRO DELICIOUS</Navbar.Brand>
                        <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-${expand}`} />
                        <Navbar.Offcanvas
                            id={`offcanvasNavbar-expand-${expand}`}
                            aria-labelledby={`offcanvasNavbarLabel-expand-${expand}`}
                            placement="end"
                        >
                            <Offcanvas.Header closeButton>
                                <Offcanvas.Title id={`offcanvasNavbarLabel-expand-${expand}`}>
                                    Offcanvas
                                </Offcanvas.Title>
                            </Offcanvas.Header>
                            <Offcanvas.Body>
                                <Nav className="justify-content-end flex-grow-1 pe-3">
                                    <Nav.Link href="/">Food</Nav.Link>
                                    <Nav.Link href="/hotal">Hotal</Nav.Link>
                                    <Nav.Link href="/travels">Travel</Nav.Link>
                                </Nav>
                                <Form className="d-flex">
                                    <Form.Control
                                        type="search"
                                        placeholder="Search"
                                        className="me-2"
                                        aria-label="Search"
                                        value={search}
                                        onChange={(e) => setSearch(e.target.value)}
                                    />
                                    <Nav.Link href={`/${search}`}>
                                        <Button variant="outline-secondary">Search</Button>
                                    </Nav.Link>
                                </Form>
                            </Offcanvas.Body>
                        </Navbar.Offcanvas>
                    </Container>
                </Navbar>
            ))}
        </>

    )
}
export default Header;
