import React, { useState } from "react";

const Hotal = () => {

    const [hotal, setHotal] = useState([
        {
            name: "Max Hotel",
            loc: "Prayagraj",
            about: "Words that best describe Pizza: Tasty: Satisfying: Versatile: Delicious: Crispy: Cheesy: Crave-worthy: Aromatic: Mouth-watering: Tempting: Comforting: Quick: ...",
            image: "https://dynamic-media-cdn.tripadvisor.com/media/photo-o/12/00/bb/d8/exterior.jpg?w=300&h=300&s=1"
        },
        {
            name: "Hotel UR",
            loc: "Allahpur Prayagraj",
            about: "Words that best describe Pizza: Tasty: Satisfying: Versatile: Delicious: Crispy: Cheesy: Crave-worthy: Aromatic: Mouth-watering: Tempting: Comforting: Quick: ...",
            image: "https://dynamic-media-cdn.tripadvisor.com/media/photo-o/1a/b7/e1/50/collection-o-46380-near.jpg?w=300&h=300&s=1"
        },
        {
            name: "Kumbh Camp Om",
            loc: "Allahpur Prayagraj",
            about: "Words that best describe Pizza: Tasty: Satisfying: Versatile: Delicious: Crispy: Cheesy: Crave-worthy: Aromatic: Mouth-watering: Tempting: Comforting: Quick: ...",
            image: "https://dynamic-media-cdn.tripadvisor.com/media/photo-o/29/5d/ff/ca/caption.jpg?w=300&h=300&s=1"
        },
    ])
    return (
        <div className="mx-2 mx-md-5 mb-3" style={{ marginTop: '100px' }}>
            <h3 className="text-center">HOTEL LIST</h3>
            {
                hotal.map((data, index) => (
                    <>
                        <div className="card mb-3">
                            <img src={`${data.image}`} className="card-img-top " alt="..." style={{ maxHeight: '400px' }} />
                            <div className="card-body">
                                <h5 className="card-title d-inline-block">Name :</h5>
                                <p className="card-text d-inline-block">
                                    {data.name}       </p>
                                <h6></h6>
                                <h5 className="card-title d-inline-block">Location : </h5>
                                <p className="card-text d-inline-block">
                                    {data.loc}       </p>
                                <h5 className="card-title">Discription: </h5>
                                <p className="card-text">
                                    {data.about}        </p>
                                <p className="card-text">
                                    <small className="text-muted">Last updated 3 mins ago</small>
                                </p>
                            </div>
                        </div>

                    </>
                ))
            }
        </div>

    )
}

export default Hotal;
