import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Alert, Button } from 'react-bootstrap';

const Login = () => {
   const [username, setUsername] = useState("");
   const [password, setPassword] = useState("");
   const [error, setError] = useState(null);
   const navigate = useNavigate();

   const loginHandler = async (event) => {
      event.preventDefault(); // Prevent default form submission
      setError(null); // Clear previous errors

      try {
         const response = await axios.post(`http://localhost:10101/userlogin`, {
            username,
            password
         });
         console.log(response.data.username)
         // Check if login was successful
         if (response?.data?.username) {
            localStorage.setItem('admin', JSON.stringify(response.data));
            const role = response.data.role;
            // Navigate based on the role
            switch (role) {
               case 'dataman':
                  navigate('/dataman');
                  break;
               case 'driver':
                  navigate('/driver');
                  break;
               case 'admin':
                  navigate('/admin');
                  break;
               case 'manager':
                  navigate('/manager');
                  break;
               default:
                  navigate('/login');
            }
         } else {
            setError("Login failed: Invalid username or password.");
         }
      } catch (err) {
         // Handle errors from the API
         setError("Login failed: " + (err.response?.data?.message || err.message));
      }
   }

   return (
      <>
         <div className="container p-4" style={{marginTop: '100px'}}>
            <div className="row p-auto m-auto">
               <div className="col-12 text-center fw-bolder">
                  <h4>USER LOGIN</h4>
               </div>
               <div className="col p-4">
                  {error && <Alert variant="danger">{error}</Alert>}
                  <form onSubmit={loginHandler}>
                     <div className="mb-3">
                        <label htmlFor="username" className="form-label">User Name</label>
                        <input className="form-control" type="text" id="username" required value={username} onChange={(e) => setUsername(e.target.value)} />
                     </div>
                     <div className="mb-3">
                        <label htmlFor="password" className="form-label">Password</label>
                        <input className="form-control" type="password" id="password" required value={password} onChange={(e) => setPassword(e.target.value)} />
                     </div>
                     <div className="d-grid gap-2 mt-4">
                        <Button variant="dark" type="submit" className="py-1" size="lg">
                           Login
                        </Button>
                     </div>
                  </form>
               </div>
            </div>
         </div>
      </>
   );
}

export default Login;
