import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import React, { useEffect, useState } from 'react'
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Category from './Categories';

const EditThali = () => {
    const params = useParams();
    const navigate = useNavigate();
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [qty, setQty] = useState("");
    const [discription, setDiscription] = useState("");
    const [edit, setEdit] = useState("");

    const findHandler = async () => {
        const data = await axios.get(`http://localhost:10101/api/v4/thali/search/${params.id}`);
        setName(data?.data[0]?.name);
        setPrice(data?.data[0]?.price);
        setImage(data?.data[0]?.image);
        setQty(data?.data[0]?.qty);
        setDiscription(data?.data[0]?.discription);
    }

    useEffect(() => {
        findHandler();
    }, []);

    const cancelHandler = () => {
        navigate('/');
    }

    const editHandler = async (event) => {
        event.preventDefault();
        const formdata = new FormData();
        formdata.append("name", name);
        formdata.append("price", price);
        formdata.append("image", image);
        formdata.append("qty", qty);
        formdata.append("discription", discription);

        const data = await axios.put(`http://localhost:10101/api/v4/thali/update/${params.id}`, formdata, {
            headers: {
                "Content-Type": "multipart/form-data"
            }
        })
        navigate('/Products');
    }

    return (
        <div className="container p-0">
            <div className="row justify-content-center">
                <div className="col-12 col-md-6">
                    <div className='container'>
                        <form method="" className=" bg-white p-2 p-md-5 mt-2" style={{
                            borderRadius: '5px',
                            boxShadow: '0px 0px 2px gray',
                        }}>
                            <div className='row'>
                                <div className="col-12">
                                    <label htmlFor="yourName" className="form-label">Thali name</label>
                                    <input type="text" name="name"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)} className="form-control"
                                        required="true"
                                    />
                                </div>
                            </div>
                            <div className='row'>
                                <div className="col-12">
                                    <label htmlFor="yourName" className="form-label">Price</label>
                                    <input type="text" name="price"
                                        value={price}
                                        onChange={(e) => setPrice(e.target.value)}
                                        className="form-control"
                                        required="true"
                                    />
                                </div>
                            </div>

                            <div className='row'>
                                <div className="col-12">
                                    <label htmlFor="yourName" className="form-label">Image</label>
                                    <input type="file" name="image"
                                        originalname={image}
                                        onChange={(event) => setImage(event.target.files[0])}
                                        className="form-control"
                                        required="true"
                                    />
                                </div>
                            </div>
                            <div className='row'>
                                <div className="col-12">
                                    <label htmlFor="yourName" className="form-label">Qty</label>
                                    <input type="number" name="qty"
                                        value={qty}
                                        onChange={(e) => setQty(e.target.value)}
                                        className="form-control"
                                    />
                                </div>
                            </div>
                            <div className='row'>
                                <div className="col-12">
                                    <label htmlFor="yourName" className="form-label">Discription</label>
                                    <input type="text" name="discription"
                                        value={discription}
                                        onChange={(e) => setDiscription(e.target.value)}
                                        className="form-control"
                                        required="true"
                                    />
                                </div>
                            </div>

                            <div className='row mt-3'>
                                <div className="col">
                                    <button class="btn btn-success my-2 my-sm-0 me-1" onClick={editHandler}>Edit</button>
                                    <button className="btn btn-danger my-2 my-sm-0" onClick={cancelHandler}>close</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>

    );
}

export default EditThali;
