import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const User = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({
    name: "",
    phone: "",
    address: ""
  });

  const userHandler = event => {
    setUser({ ...user, [event.target.name]: event.target.value });
  }

  const submitHandler = async event => {
    event.preventDefault();
    const user_data = await axios.post(`http://localhost:10101/api/v1/user/create`, user);
    if(user_data?.data.Massage == 'success') {
        localStorage.setItem('auth', JSON.stringify('success'));
        navigate('/');
    }
    
  }

  const cancelHandler = () => {
    localStorage.setItem('visit', JSON.stringify(1));
    navigate('/');
  }

  return (
    <div className="container p-0" style={{ marginTop: '100px' }}>
      <div className="row justify-content-center">
        <div className="col-12 col-md-6">
          <div className='container'>
            <form onSubmit={submitHandler} className=" bg-white p-1 p-md-5 mt-2" style={{
              borderRadius: '5px',
              boxShadow: '0px 0px 1px gray'
            }}>
              <div className='row'>
                <div className="col-12">
                  <label htmlFor="yourName" className="form-label">Name</label>
                  <input type="text" name="name"
                    value={user.name}
                    onChange={userHandler} className="form-control"
                    required="true"
                  />
                </div>
              </div>


              <div className='row'>
                <div className="col-12">
                  <label htmlFor="yourName" className="form-label">Phone</label>
                  <input type="text" name="phone"
                    value={user.phone}
                    onChange={userHandler}
                    className="form-control"
                    required="true"
                  />
                </div>
              </div>

              <div className='row'>
                <div className="col-12">
                  <label htmlFor="yourName" className="form-label">Address</label>
                  <input type="text" name="address"
                    value={user.address}
                    onChange={userHandler}
                    className="form-control"
                    required="true"
                  />
                </div>
              </div>

              <div className='row mt-3'>
                <div className="col">
                  <button class="btn btn-primary my-2 my-sm-0 me-1" type="submit">register</button>
                  <button className="btn btn-danger my-2 my-sm-0" onClick={cancelHandler}>skip</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>

  )
}
export default User;
