import axios from "axios";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import './App.css';

const User = () => {
  const [pass, setPass] = useState(false);
  const [alert, setAlert] = useState(false);
  const navigate = useNavigate();
  const [user, setUser] = useState({
    name: "",
    phone: "",
    address : ""
  });

  const userHandler = event => {
    setUser({ ...user, [event.target.name]: event.target.value });
  }

  const submitHandler = async event => {
    event.preventDefault();
    if (user.confirmpassword == user.password) {
      const userexist = await axios.get(`${window.location.origin}/api/v1/user/search/${user.email}`, user);
      
      if (userexist?.data) {
        setAlert(true);
      } else {
        const data = await axios.post(`${window.location.origin}/api/v1/user/create`, user);
        if (data) {
          navigate('/');
        } 
      }
    } else {
      setPass(true);
    }
  }

  const cancelHandler = () => {
    navigate('/');
  }

  return (
    <div className="container p-0" style={{marginTop: '100px'}}>
      <div className="row justify-content-center">
        <div className="col-12 col-md-6">
          <div className='container'>
          {
        alert ? <div className="alert alert-danger" role="alert">
          This Acount Already Exist? Please <Link to='/UserLogin' className="text-decoration-none fw-bold">Login</Link>
        </div> : ""
      }
            <form onSubmit={submitHandler} className=" bg-white p-2 p-md-5 mt-2" style={{
              borderRadius: '5px',
              boxShadow: '0px 0px 1px gray'
            }}>
              <div className='row'>
                <div className="col-12">
                  <label htmlFor="yourName" className="form-label">Name</label>
                  <input type="text" name="name"
                    value={user.name}
                    onChange={userHandler} className="form-control"
                    required="true"
                  />
                </div>
              </div>
        
       
              <div className='row'>
                <div className="col-12">
                  <label htmlFor="yourName" className="form-label">Phone</label>
                  <input type="number" name="phone"
                    value={user.phone}
                    onChange={userHandler}
                    className="form-control"
                    required="true"
                  />
                </div>
              </div>

              <div className='row mt-3'>
                <div className="col">
                  <button class="btn btn-primary my-2 my-sm-0 me-1" type="submit">register</button>
                  <button className="btn btn-danger my-2 my-sm-0" onClick={cancelHandler}>skip</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>

  )
}
export default User;
