import React, { useState } from 'react';
import axios from 'axios';

const Admin = () => {
    const [admin, setAdmin] = useState({
        name: "",
        password: ""
    });

    const userHandler = (event) => {
        const { name, value } = event.target;
        setAdmin((prevAdmin) => ({
            ...prevAdmin,
            [name]: value
        }));
    };

    const loginHandler = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.get(`http://localhost:10101/api/v2/admin`, admin);
            // Handle successful login here (e.g., store token, redirect, etc.)
            console.log(response.data);
        } catch (error) {
            if (error.response) {
                console.error('Login error:', error.response.data);
            } else {
                console.error('Unexpected error:', error.message);
            }
        }
    };

    return (
        <>
            <div className='w-75 w-md-50 container-fluid bg-white p-3 p-md-4' style={{
                borderRadius: '5px',
                boxShadow: '0px 0px 1px gray',
                marginTop: '100px'
            }}>
                <div className='d-flex justify-content-center'>
                    <img src="/metro-logo.jpg" alt="..." style={{maxHeight:'100px',maxWidth: '100px'}}/>
                </div>
                <div className='d-flex justify-content-center'>
                    <span>Admin Panel</span>
                </div>
                <div className='container w-sm-75 w-lg-50 p-3' style={{ boxShadow: '0px 0px 2px warning' }}>
                    <form onSubmit={loginHandler}>
                        <div className='row'>
                            <div className="col-12">
                                <label htmlFor="yourName" className="form-label">User name</label>
                                <input 
                                    type="text" 
                                    name="name"
                                    value={admin.name}
                                    onChange={userHandler}
                                    className="form-control"
                                    required
                                />
                            </div>
                        </div>

                        <div className='row'>
                            <div className="col-12">
                                <label htmlFor="yourPassword" className="form-label">Password</label>
                                <input 
                                    type="password" 
                                    name="password"
                                    value={admin.password}
                                    onChange={userHandler}
                                    className="form-control"
                                    required
                                />
                            </div>
                        </div>

                        <div className='row mt-3'>
                            <div className="col">
                                <button type="submit" className="btn btn-success my-2 my-sm-0">Login</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </>
    );
}

export default Admin;
